DROP TABLE RPT_SITE_FROM_CDJW CASCADE CONSTRAINTS PURGE;
CREATE TABLE RPT_SITE_FROM_CDJW 
(	BUSINESS_DATE			DATE,
	SN						VARCHAR2(300)  ,
	SITE_NAME				VARCHAR2(300)  ,
	SITE_FROM				VARCHAR2(300)  ,
	SITE_LAND_NO			VARCHAR2(300)  ,
	REGION_AREA				VARCHAR2(300)  ,
	CIRCLE_AREA				VARCHAR2(300)  ,
	SITE_ADDRESS			VARCHAR2(300)  ,
	SETUP_YEAR				VARCHAR2(300)  ,
	SITE_TYPE_PLAN			VARCHAR2(300)  ,
	SITE_TYPE_ACTUAL		VARCHAR2(300)  ,
	LONGITUDE				VARCHAR2(60)   ,
	LATITUDE				VARCHAR2(60)   ,
	SITE_SQUARE				VARCHAR2(300)  ,
	PARKING_SQUARE			VARCHAR2(300)  ,
	FACILITY_SQUARE			VARCHAR2(300)  ,
	MAX_PARKING				VARCHAR2(300)  ,
	OPER_ROUTE				VARCHAR2(300)  ,
	OPER_ROUTE_COUNT		VARCHAR2(300)  ,
	PARKING_GAP				VARCHAR2(300)  ,
	CONNECT_WITH			VARCHAR2(300)  ,
	AREA_CODE				VARCHAR2(300)  ,
	REMARKS					VARCHAR2(200)
	) 
TABLESPACE TBS_OSECDJW_RPT
NOLOGGING;



COMMENT ON TABLE RPT_SITE_FROM_CDJW  IS '交委提供场站基础数据表';

COMMENT ON COLUMN RPT_SITE_FROM_CDJW.SN					IS '编号';
COMMENT ON COLUMN RPT_SITE_FROM_CDJW.SITE_NAME			IS '场站名称';
COMMENT ON COLUMN RPT_SITE_FROM_CDJW.SITE_FROM			IS '权属情况';
COMMENT ON COLUMN RPT_SITE_FROM_CDJW.SITE_LAND_NO		IS '宗地编号（属48宗的）';
COMMENT ON COLUMN RPT_SITE_FROM_CDJW.REGION_AREA		IS '所属行政区';
COMMENT ON COLUMN RPT_SITE_FROM_CDJW.CIRCLE_AREA		IS '所属环域';
COMMENT ON COLUMN RPT_SITE_FROM_CDJW.SITE_ADDRESS		IS '场站具体位置';
COMMENT ON COLUMN RPT_SITE_FROM_CDJW.SETUP_YEAR			IS '建成时间';
COMMENT ON COLUMN RPT_SITE_FROM_CDJW.SITE_TYPE_PLAN		IS '规划功能定位（类型及等级）';
COMMENT ON COLUMN RPT_SITE_FROM_CDJW.SITE_TYPE_ACTUAL	IS '功能使用现状（类型及等级）';
COMMENT ON COLUMN RPT_SITE_FROM_CDJW.LONGITUDE			IS '北纬';
COMMENT ON COLUMN RPT_SITE_FROM_CDJW.LATITUDE			IS '东经';
COMMENT ON COLUMN RPT_SITE_FROM_CDJW.SITE_SQUARE		IS '用地面积（㎡）';
COMMENT ON COLUMN RPT_SITE_FROM_CDJW.PARKING_SQUARE		IS '公交停车场建筑面积（含地下室㎡）';
COMMENT ON COLUMN RPT_SITE_FROM_CDJW.FACILITY_SQUARE	IS '公交配套设施建筑面积（含地下室㎡）';
COMMENT ON COLUMN RPT_SITE_FROM_CDJW.MAX_PARKING		IS '（含夜间停车）最大静态停放（标台）';
COMMENT ON COLUMN RPT_SITE_FROM_CDJW.OPER_ROUTE			IS '运营线路';
COMMENT ON COLUMN RPT_SITE_FROM_CDJW.OPER_ROUTE_COUNT	IS '现有营运线路（条）';
COMMENT ON COLUMN RPT_SITE_FROM_CDJW.PARKING_GAP		IS '车位缺口（标台）';
COMMENT ON COLUMN RPT_SITE_FROM_CDJW.CONNECT_WITH		IS '与长途汽车站、地铁、火车站驳接情况';
COMMENT ON COLUMN RPT_SITE_FROM_CDJW.AREA_CODE  		IS '区域类型';
COMMENT ON COLUMN RPT_SITE_FROM_CDJW.REMARKS			IS '备注';

	
delete from RPT_SITE_FROM_CDJW;
commit;
commit;




update rpt_site_from_cdjw a
set (longitude, latitude) =  
						(select b.longitude, b.latitude
						from map_site_station b
						where a.site_name = b.site_name
						and b.longitude is not null
						and rownum = 1
						);
commit;
